import '../styles/Task.css';

import React, { useState } from 'react';

const Task = ({ onAddTask }) => {
  const [newTask, setNewTask] = useState('');

  const handleInputChange = (e) => {
    setNewTask(e.target.value);
  };

  const handleAddTask = () => {
    if (newTask.trim() !== '') {
      onAddTask(newTask); // Llama a la función proporcionada desde TaskList.js
      setNewTask(''); // Limpia el campo de entrada
    }
  };

  const handleInputKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleAddTask();
    }
  };

  return (
    <div>
      <input
        type="text"
        id="taskInput"
        placeholder="Nueva tarea"
        value={newTask}
        onChange={handleInputChange}
        onKeyPress={handleInputKeyPress} // Agrega esta línea
      />
      <button id="addTaskButton" onClick={handleAddTask}>Agregar tarea</button>
    </div>
  );
};

export default Task;
