import React, { useState } from 'react';
import '../styles/App.css';
import '../styles/Task.css';

function TaskList() {
  const [tasks, setTasks] = useState([]);
  const [taskInput, setTaskInput] = useState('');

  const updateTaskList = () => {
    const taskList = document.getElementById('taskList');
    taskList.innerHTML = '';
    tasks.forEach((task, index) => {
      const taskElement = document.createElement('div');
      taskElement.className = 'task';
      taskElement.draggable = true;
      taskElement.setAttribute('data-index', index);

      const taskText = document.createElement('span');
      taskText.className = 'task-text';
      taskText.textContent = task;

      const deleteButton = document.createElement('button');
      deleteButton.className = 'delete-button';
      deleteButton.textContent = 'X';

      deleteButton.addEventListener('click', () => {
        const confirmed = window.confirm('¿Quieres borrar esta tarea?');
        if (confirmed) {
          tasks.splice(index, 1);
          updateTaskList();
        }
      });

      taskElement.appendChild(taskText);
      taskElement.appendChild(deleteButton);

      taskElement.addEventListener('dragstart', (e) => {
        e.dataTransfer.setData('text/plain', index);
      });

      taskList.appendChild(taskElement);
    });
  };

  const addTask = () => {
    const newTask = taskInput.trim();
    if (newTask !== '') {
      tasks.push(newTask);
      setTaskInput('');
      updateTaskList();
    }
  };

  return (
    <div className="caja">
      <span>
        <h1>Lista de Tareas</h1>
        <div>
          <input
            type="text"
            value={taskInput}
            onChange={(e) => setTaskInput(e.target.value)}
            placeholder="Nueva tarea"
          />
          <button onClick={addTask}>Agregar tarea</button>

          <div className="buttonimagen">
            <span>
              <button id="sortButton"> <img className="imagen" src="A_Z.png" alt="organizador " height="25px" /> </button>
            </span>
          </div>

          <div id="taskList">
            {/* Aquí mostrarás la lista de tareas */}
          </div>
        </div>
      </span>
    </div>
  );
}

export default TaskList;
